import Link from "next/link";

const Pagination = (props) => {
  const { next, prev, route } = props;
  
  if(next){
    const nextPage = next.slice(-1);
    const nextRoute = `/${route}?page=${nextPage}`
    console.log(nextRoute);
  }

  if(prev){
    console.log(prev.slice(-1));
  }
  return (
    <nav aria-label="Page navigation example">
      <ul className="pagination" style={{textAlign:"center"}}>
        {prev && <li className="page-item">
          <a className="page-link" >
            Previous
          </a>
        </li>}
        
        {next && <li className="page-item">
         
            <a className="page-link" >
              Next
            </a>
         
        </li>}
      </ul>
    </nav>
  );
};

export default Pagination;
