import Router from "next/router";

const Cards = (props) => {
  return (
    <div className="d-flex flex-wrap mx-auto" style={{textAlign:"center"}}>
      {props.results.map((data, index) => {
        return (
          <div className="card" style={{ width: "18rem", padding:"15px" }} key={index}>
            <img src={data.image} className="card-img-top" alt={data.name} />
            <div className="card-body">
              <h5 className="card-title">Name: {data.name}</h5>

              <a
                className="btn btn-primary"
                onClick={(e) =>
                  Router.push(
                    `/${props.route}/[id]`,
                    `/${props.route}/${data.id}`
                  )
                }
              >
                More Info...
              </a>
            </div>
          </div>
        );
      })}
    </div>
  );
};

export default Cards;
