import Router from "next/router";

const Cards = (props) => {
  return (
    <div className="contentCards" style={{ textAlign: "center" }}>
      {props.results.map((data, index) => {
        return (
          <div
            className="card"
            style={{ width: "300px", margin: "15px" }}
            key={index}
          >
            {data.image && (
              <img src={data.image} className="card-img-top" alt={data.name} />
            )}
            {!data.image &&(
                <img src="https://rickandmortyapi.com/api/character/avatar/19.jpeg" alt="genericImage"/>
            )}
            <div className="card-body">
              <h5 className="card-title">Name: {data.name}</h5>

              <a
                className="btn btn-primary"
                onClick={(e) =>
                  Router.push(
                    `/${props.route}/[id]`,
                    `/${props.route}/${data.id}`
                  )
                }
              >
                More Info...
              </a>
            </div>
          </div>
        );
      })}
    </div>
  );
};

export default Cards;
