import { useRouter } from "next/router";
import Container from "../../components/Container";

import Global from "../../services/Global";
import axios from "axios";

const Character = (props) => {
  const router = useRouter();
  let { id } = router.query;

  const { character } = props;

  return (
    <Container title={character.name}>
      <div className="card mb-3">
        <img src={character.image} className="card-img-top" alt={character.name} />
        <div className="card-body">
          <h5 className="card-title">{character.name}</h5>
          <p className="card-text">{character.species}</p>
          <p className="card-text"><small className="t}ext-muted">Last updated 3 mins ago</small></p>
        </div>
        <div className="btn btn-primary">Back</div>
      </div>
    </Container>
  );
};

Character.getInitialProps = async (ctx) => {
  try {
    const res = await axios.get(`${Global.url}character/${ctx.query.id}`);
    const resultsData = res.data;
    return { character: resultsData };
  } catch (error) {
    return { error };
  }
};

export default Character;
