import Container from "../components/Container";
import Cards from '../components/Cards';

import Global from "../services/Global";
import axios from "axios";

const SearchCharacter = (props) => {
    console.log(props);
  if (props.allres) {
    var { info, results } = props.allres;
  }
  return (
    <Container>
      <Cards results={results} route="characters" />
    </Container>
  );
};

SearchCharacter.getInitialProps = async (ctx) => {
  const { name } = ctx.query;
  try {
    if (ctx.query.name) {
      const res = await axios.get(`${Global.url}character/?name=${name}`);
      const resultsData = res.data;
      return { allres: resultsData };
    } else {
      const res = await axios.get(`${Global.url}character`);
      const resultsData = res.data;
      return { allres: resultsData };
    }
  } catch (error) {
    return { error };
  }
};

export default SearchCharacter;
